import {
  bundle
} from "./chunk-T7X2DBG5.js";

// src/cli.ts
import { cac } from "cac";

// package.json
var version = "0.0.29";

// src/cli.ts
var cli = cac("hattip-deno");
cli.command(
  "<input> <output>",
  "Bundle the HatTip app in <input> into <output> as a Deno module"
).option(
  "-s, --staticDir <dir>",
  "Static files directory to copy next to the output"
).action(
  async (input, output, { staticDir }) => {
    await bundle({ input, output, staticDir });
  }
);
cli.help();
cli.version(version);
cli.parse();
